package LojaVirtual;

public class Produto {

	private String nome;
	private double valor;

	public Produto(String nome, double valor) {
		// TODO Auto-generated constructor stub
		this.nome = nome;
		this.valor = valor;
	}

	public String getNome() {
		return nome;
	}

	public double getValor() {
		// TODO Auto-generated method stub
		return valor;
	}

}
